/**
 * 点（元组表示）
 */
export type PointTuple = [number, number];

/**
 * 点（对象表示）
 */
export type Point = {
  x: number;
  y: number;
};

/**
 * 包围盒
 */
export type BBox = {
  x: number;
  y: number;
  width: number;
  height: number;
};

/**
 * 一条线段
 */
export type Segment = {
  from: PointTuple;
  to: PointTuple;
  length: number;
};
